import React from "react"
import {Button, ScrollView,Text,Vibration,View} from "react-native"
import {Card,CardTitle,CardContent, CardAction,CardButton, CardImage} from "react-native-cards"
class AnimationView extends React.Component{
    state={
      Viewshown:true
    }


      componentDidMount(){
//   // setInterval(this.toggleShow,1000)
  for(let index=1; index <5 ; index ++){

setTimeout(this.toggleShow,300)
setTimeout(this.toggleShow,900)
setTimeout(this.toggleShow,1200)
  }}


toggleShow=()=>{

  this.setState(prevstate=>({Viewshown:!prevstate.Viewshown}))
  
}





    render(){
   

    
    
return(
<ScrollView>
  {
    this.state.Viewshown ?
  <Card>
   <CardImage source={require("./assets/emptyshoppingcart.png")}  
   title="no item added to cart"
   />
   <CardTitle  
   subtitle="cart is empty"/>
   <CardContent text="please add an item to get started"/>
  <CardAction
  separator={true}
  inColumn={false}
  >
{/* <CardButton  
onPress={()=>this.props.navigation.goBack()}
title="EXPLORE"
color="#FEB557"

/> */}

  </CardAction>
  </Card>
  



    :<View></View>
  }

</ScrollView> 
)
}



}
export default AnimationView
