import AnimationView from "./animationView";
import Checkout from "./checkout"
import React from 'react';
import { View, Text, Button,ScrollView,TouchableOpacity,StyleSheet,Dimensions,TextInput } from 'react-native';
class  RenderCheckoutScreen extends React.Component{
//   static navigationOptions={
//     headerShown:false,
   
//  tabBarVisible:false
//   }




 state={ 
   showcontact:false,
   showfirstView:true,
   balance:false
}

dynamicPayment=()=>{
if(this.state.balance){
alert("shh")
}else{
  return(
   alert("hshs")
  ) 
}
}




dynamicView=()=>{
  if(this.props.screenProps.addedItem.length <=0){

    return(
      <View>
     
     

    <AnimationView/>
    </View>
    )

  }else{
return (
 
 <View>

    
  {/* {this.state.showcontact ?    <View style={styles.addpayment} >
  <Text  > Add a payment method to Checkout</Text> 
 <TextInput placeholder="csv" style={styles.csv} keyboardType="numeric"/>
 <TextInput  placeholder="expiry date" style={styles.expirydate} keyboardType="numeric"/>
<TextInput placeholder="pin" style={styles.pin} keyboardType="numeric"/>
<TouchableOpacity><Text style={styles.checkout}>checkout</Text></TouchableOpacity>
    </View>  : <TouchableOpacity>
       <Text style={styles.paymentmethod} onPress={this.changepayment}>Make payment</Text>
       </TouchableOpacity>} */}

        {/* {this.state.showcontact ?    <View style={styles.addpayment} >
 
<TouchableOpacity onPress={this.dynamicPayment}><Text style={styles.checkout}>pay with available balance</Text></TouchableOpacity>
                            <Text>OR</Text>
<TouchableOpacity><Text style={styles.debitcardpayment}>pay with debit card</Text></TouchableOpacity>
    </View> 
    
    
    : <TouchableOpacity>
       <Text style={styles.paymentmethod} onPress={this.changepayment}>Make payment</Text>
       </TouchableOpacity>}

 </View> */}


      <View style={styles.addpayment} >
 {
this.state.showcontact ?    <View style={styles.addpayment} >
  <Text  > Add a payment method to Checkout</Text> 
 <TextInput placeholder="csv" style={styles.csv} keyboardType="numeric"/>
 <TextInput  placeholder="expiry date" style={styles.expirydate} keyboardType="numeric"/>
<TextInput placeholder="pin" style={styles.pin} keyboardType="numeric"/>
<TouchableOpacity><Text style={styles.checkout}>checkout</Text></TouchableOpacity>
    </View>

 :
 <View>
<TouchableOpacity onPress={this.dynamicPayment}><Text style={styles.checkout}>pay with available balance</Text></TouchableOpacity>
                            <Text>OR</Text>
<TouchableOpacity onPress={this.changepayment}><Text style={styles.debitcardpayment}>pay with debit card</Text></TouchableOpacity>
</View>
  }
    </View> 
 
 </View>


)
  }

}




changepayment=()=>{
  this.setState({showcontact:true})
      }
  render(){
    return(
<ScrollView style={{flex:1}}>
   
    { 
    this.props.screenProps.addedItem.map(
      addedItem=><Checkout key={addedItem.id} price={addedItem.price} name={addedItem.name} source={addedItem.source}
      onIncrement={this.props.screenProps.onIncrement}
      onDecrement={this.props.screenProps.onDecrement}
      id={addedItem.id}
      addedItem={addedItem}
      onDelete={this.props.screenProps.onDelete}
      />
      )
    }
   
   
{this.dynamicView()}
</ScrollView>
    )
  }
}



const styles=StyleSheet.create({
  checkout:{
    
    backgroundColor:"green",
    width:Dimensions.get("window").width,
    height:35,
    borderRadius:50,
    alignItems:"center",
     textAlign:"center",
    color:"white",
    paddingTop:7
  },
  addpayment:{
 
alignItems:"center",
paddingTop:5,

  },

  csv:{
// backgroundColor:"gray",
    // height: 35,
    textAlign: 'center',
   
    width: Dimensions.get('window').width ,
    borderRadius: 50,
    borderColor: 'gray',
   borderWidth:2

  },
  expirydate:{
    // backgroundColor:"gray",
          // height: 35,
          textAlign: 'center',
         
          width: Dimensions.get('window').width ,
          borderRadius: 50,
          borderColor: 'gray',
         borderWidth:2,
         margin:2
      
        },
        pin:{
          // backgroundColor:"gray",
                // height: 35,
                textAlign: 'center',
               
                width: Dimensions.get('window').width ,
                borderRadius: 50,
                borderColor: 'gray',
               borderWidth:2,
               margin:2
            
              },

  paymentmethod:{
    textAlign: 'center',
    margin: 4,
alignSelf:"center",
    width: Dimensions.get('window').width ,
    height: 35,
    fontSize: 18,
 color:"green",
    paddingTop: 4,
    borderColor: 'green',
    borderRadius: Platform.OS === 'ios' ? 17 : 50,
    borderWidth: 3,
    overflow: 'hidden',
    
   
  },


    debitcardpayment:{
    textAlign: 'center',
  color:"gray",
  margin:4
   
  },
 
 
  
})
export default RenderCheckoutScreen
