import React from "react"
import {Button, 
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  View,
  Image,
  Text

} from "react-native"
import {Card,CardTitle,CardContent, CardAction,CardButton, CardImage} from "react-native-cards"
class Transactions extends React.Component{





    render(){
    
return(
 
<ScrollView style={styles.container}>

{/* <View>

  
  </View> */}

 <TouchableOpacity onPress={()=>this.props.navigation.navigate("Profiledeposit")}>



<View style={styles.containerview}>
<Image source={ require("../assets/money.png")} style={ styles.img}/>

<Text style={{fontSize:20,fontWeight:"bold",color:"#214c63"}} >Available balance:${this.props.screenProps.balance} </Text>
   {/* < Text  style={styles.text}>No Available Balance at the moment</Text> */}
   { this.props.screenProps.balance <=0 ? < Text  style={styles.text}>No Available Balance at the moment</Text> : < Text  style={styles.text}> balance Available :${this.props.screenProps.balance}</Text>}
<Text style={styles.button}   >Make a Deposit</Text>
</View>

</TouchableOpacity>




  

<TouchableOpacity onPress={()=>this.props.navigation.navigate("Withdrawal")}>
  <View style={styles.containerview}>

<Image source={ require("../assets/atm-withdrawal.png")} style={ styles.img}/>

< Text style={{fontSize:20,fontWeight:"bold",color:"#214c63"}}> Available Withdrawals: ${this.props.screenProps.balance} </Text>
   < Text  style={styles.text}  >withdrawals take 15 to 20 minutes to complete</Text>
<Text  style={styles.text}>Make a withdrawal</Text>
  
<Text style={styles.button} >Make Withdrawals</Text>
</View>
  
</TouchableOpacity>







   
  

</ScrollView> 
)
}



}

const styles=StyleSheet.create({
//   view:{
//   textAlign:"center",
 
//   },
//   card:{
// margin:10
//   },
//   text:{
//     padding:4
//      ,color:"gray",
//      fontSize:15,
//      fontWeight:"bold"}
container:{
     flex:1,
backgroundColor:"#E5E5E5",
},

containerview:{
   alignItems:"center",  
marginTop:20,
backgroundColor:"#fff",
height:390,
width:"85%",
alignSelf:"center",
borderColor:"#e1e8ed",
borderWidth:1,
// justifyContent:"center"
},
img:{
  height:200,
  width:"100%"
},
text:{
textAlign:"center"
},
button:{
  
  backgroundColor: 'green',
    alignSelf: 'center',
    width: "95%",
    // marginLeft: Dimensions.get('window').width /8,
    height: 35,
    borderRadius: Platform.OS==="ios" ? 17 :50,
 
    paddingTop: 6,
    fontSize: 18,
    textAlign: 'center',
    margin:7,
    color: 'white',
    overflow:"hidden"
}
})
export default Transactions
