 },
 text:{
   textAlign:"center",
  //  fontStyle:"normal",
  //  opacity:0.7,
  //  fontWeight:"bold"
  },
  messageheading:{
   fontSize:20
   ,fontWeight:"bold",
   opacity:0.7,
   padding:5,
   color:"#214c63",
   margin:4
  },

 heading:{
   fontSize:20
   ,fontWeight:"bold",
  //  opacity:0.7,
   padding:5,
   color:"#214c63"
  },
 button: {
 
    backgroundColor: 'green',
    alignSelf: 'center',
    width: Dimensions.get("window").width/1.5,
    // marginLeft: Dimensions.get('window').width /8,
    height: 35,
    borderRadius: Platform.OS==="ios" ? 17 :50,
 
    paddingTop: 6,
    fontSize: 18,
    textAlign: 'center',
    margin:20,
    color: 'white',
    overflow:"hidden"
  },
  withdrawal:{
    color:"gray",
   fontWeight:"bold"
  },
  text2:{
    textAlign:"center",
   fontStyle:"normal",
   opacity:0.7,
   fontWeight:"bold",
   marginTop:20
  }
  
})
export default AnimationView









































































