import React from "react"
import {
    View,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    StyleSheet,
    Dimensions
}from "react-native"

class Withdrawal extends React.Component{
state={
   amount:"Amount",
bank:"bank",
Account:"Account number "
}





    dynamicColorInsertion=()=>{
       if( this.props.screenProps.balance <=0){
           return{
               textAlign:"center",
         fontSize:20,fontWeight:"bold",
         color:"red" 
           }
       }else{
           return{
               textAlign:"center",
         fontSize:20,fontWeight:"bold",
         color:"green"  
           }
       }
    }
    render(){
        return(
          
<ScrollView style={styles.container}>
     <Text 
     style={this.dynamicColorInsertion()}>Balance:${this.props.screenProps.balance}</Text>

  <View style={styles.mainview}>
     
<Text style={{   textAlign:"center",fontSize:20,fontWeight:"bold",color: 'green'}} >Make withdrawals</Text>
<TextInput placeholder="amount" style={styles.textinput} keyboardType="numeric" value={this.state.amount} onChangeText={()=>alert("jsj")}/>
<TextInput placeholder="bank" style={styles.textinput} value={this.state.bank}/>
<TextInput placeholder="Account Number" style={styles.textinput} keyboardType="numeric" value={this.state.Account}/>

<TouchableOpacity >
<Text style={styles.button}>Withdraw</Text>
</TouchableOpacity>
  </View>
</ScrollView>
         
        )
    
    
    }
}

const styles=StyleSheet.create( {
container:{
    flex:1,
backgroundColor:"#E5E5E5",

},
mainview:{
 alignItems:"center",  
marginTop:20,
backgroundColor:"#fff",
height:390,
width:"85%",
alignSelf:"center",
borderColor:"#e1e8ed",
borderWidth:1,
justifyContent:"center"
},
textinput:{
  margin:5,
    textAlign: 'center',
    // color: 'white',
    width:"85%" ,
    borderRadius: 50,
    borderColor: 'gray',
    borderWidth: 1,
    alignSelf:"center"
},


  button: {
    backgroundColor: 'green',
    alignSelf: 'center',
    width: Dimensions.get("window").width/1.5,
    // marginLeft: Dimensions.get('window').width /8,
    height: 35,
    borderRadius: Platform.OS==="ios" ? 17 :50,
 
    paddingTop: 6,
    fontSize: 18,
    textAlign: 'center',
    margin:7,
    color: 'white',
    overflow:"hidden"
  },
    })
export default Withdrawal
